import React from 'react'

const Header = (props) => {  // renders name of course
  return(<h1>{props.course}</h1>);

}
const Content = (props) => { // renders the parts and number of exercises
  let content = [];
  for (let i = 0; i < props.parts.length; i++) {
    content.push(<p>{props.parts[i]} {props.exercises[i]}</p>);
  }
  return(<>{content}</>);

}
const Total = (props) => { // renders total number of exercises
  const total = props.exercises.reduce((sum, ex) => sum += ex, 0);
  return(<p>Number of exercises {total}</p>);

}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  return (
    <div>
      <Header course={course} />
      <Content parts={[part1, part2, part3]} exercises={[exercises1, exercises2, exercises3]} />
      <p>
        {part1} {exercises1}
      </p>
      <p>
        {part2} {exercises2}
      </p>
      <p>
        {part3} {exercises3}
      </p>
      <p>Number of exercises {exercises1 + exercises2 + exercises3}</p>
    </div>
  )
}

export default App
